<?php$objTemplate = new Template();// Titulo padro da pagina;$objTemplate->setTitle('Documento sem Ttulo');// Configurao$objTemplate->dir = TEMPLATE_DIR;$objTemplate->cachedir = TEMPLATE_CACHE_DIR;$objTemplate->debug = TEMPLATE_DEBUG;$objTemplate->cache = TEMPLATE_CACHE;$objTemplate->cacheTime = TEMPLATE_CACHE_TIME;// Configura o Template;$objTemplate->config();$listaCursos = array();$xmlCursos = @simplexml_load_file('http://www.academiainfoclad.com.br/site_antigo/xmlCursos');if ($xmlCursos == true) {    foreach ($xmlCursos->Curso as $curso) {        $curso->Nome = utf8_decode($curso->Nome);        $curso->URL = "http://www.academiainfoclad.com.br/curso-{$curso->Id}";        $listaCursos[] = $curso;    }}$siteBase = $_SERVER['HTTP_HOST'] == "localhost" ? 'http://localhost/infoclad/site/' : 'http://www.infoclad.com.br/';$feed = array();$xml = array();$xml[] = "http://rss.idgnow.com.br/c/32184/f/499647/index.rss"; // Segurana$xml[] = "http://rss.idgnow.com.br/c/32184/f/499646/index.rss"; // Internet$xml[] = "http://rss.idgnow.com.br/c/32184/f/499648/index.rss"; // Redes$xml[] = "http://rss.idgnow.com.br/c/32184/f/499643/index.rss"; // Computao Corporativaforeach ($xml as $rssURL) {    $xmlContent = @simplexml_load_file($rssURL);    if (is_object($xmlContent) == true) {        foreach ($xmlContent->channel->item as $conteudo) {            $feed[] = array('Titulo' => utf8_decode($conteudo->title), 'Data' => strtotime($conteudo->pubDate), 'Link' => $conteudo->link, 'Descricao' => strip_tags(utf8_decode($conteudo->description)));            break;        }    }}$objTemplate->assign('siteBase', $siteBase);$objTemplate->assign('feedNoticias', $feed);$objTemplate->assign('Cursos', $listaCursos);?>